pragma solidity ^0.5.8;

contract Storage {
    event FileStoredBy(address owner);

    struct File {
        address owner;
        string hash;
        address[] shareWith;
        uint amount;
    }
    File[] private files;

    function store(string memory hash, address[] memory shareWith, uint amount) public {
        files.push(File(msg.sender, hash, shareWith, amount));
        emit FileStoredBy(msg.sender);
    }
}
